import React, { useState, useEffect } from "react";
import "./styles.css"; // Pastikan untuk mengimpor CSS

const useDarkMode = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  return [darkMode, setDarkMode];
};

const DarkMode = () => {
  const [darkMode, setDarkMode] = useDarkMode();

  return (
    <div className="dark-mode">
      <input type="checkbox" className="theme-checkbox w-25 h-12 bg-gradient-to-r from-gray-200 to-gray-900 rounded-full cursor-pointer" checked={darkMode} onChange={() => setDarkMode(!darkMode)} />
    </div>
  );
};

export default DarkMode;
