import React from "react";
import GridLayout from "react-grid-layout";
import "react-grid-layout/css/styles.css";
import "react-resizable/css/styles.css";
import { GridItem, GridItemWithImage } from "./GridItem";

export const GridContainer = ({ layout, items }) => {
  const renderedItems = items.map((item, index) => {
    // Tentukan apakah item menggunakan gambar atau tidak
    if (item.image) {
      return <GridItemWithImage key={index} {...item} />;
    }
    return <GridItem key={index} {...item} layout={layout} />;
  });

  return (
    <GridLayout className="layout" layout={layout} cols={12} width={1200} isResizable={false} draggableHandle=".draggable-handle">
      {renderedItems}
      <div key="b" className="bg-white border border-gray-300 flex items-center justify-center text-lg">
        <span className="draggable-handle cursor-move p-2">B</span>
      </div>
    </GridLayout>
  );
};
