import React, { useState, useEffect } from "react";
import "./style.css"; // Pastikan untuk mengimpor CSS untuk pseudo-elemen

const useDarkMode = () => {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [darkMode]);

  return [darkMode, setDarkMode];
};

const DarkMode = () => {
  const [darkMode, setDarkMode] = useDarkMode();

  return (
      <input
        type="checkbox"
        className="theme-checkbox appearance-none w-24 h-12 bg-gradient-to-r from-gray-200 to-gray-800 rounded-full relative cursor-pointer transition-all duration-400"
        checked={darkMode}
        onChange={() => setDarkMode(!darkMode)}
      />
  );
};

export default DarkMode;
