import React from "react";
import { Footer } from "./Components/Footer/Footer";
import DarkModeButton from "./Utils/DarkMode/DarkModeButton";

function App() {
  useEffect(() => {
    const navItems = document.querySelectorAll(".nav-item");
    const handleScroll = () => {
      navItems.forEach((item) => {
        const link = item.querySelector("a").getAttribute("href");
        const target = document.querySelector(link);
        const pixel = 480;
        if (target.getBoundingClientRect().top <= pixel && target.getBoundingClientRect().bottom >= pixel) {
          item.classList.add("active");
        } else {
          item.classList.remove("active");
        }
      });
    };

    window.addEventListener("scroll", handleScroll);

    // Cleanup function
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <nav className="navbar shadow-sm bg-blue-400 dark:bg-blue-800">
        <DarkModeButton />
        <div className="flex justify-between p-5">
          <a href="">Ignatius Alexander Hery</a>
          <div className="">
            <ul className="flex justify-evenly gap-4">
              <li className="active:">
                <a className="nav-link" href="#Home">
                  Home
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#About">
                  About
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#Projects">
                  Projects
                </a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#Contact">
                  Contact Me
                </a>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <main className="main">
        <section id="Home" className="min-h-screen">
          Home
        </section>
        <section id="About" className="min-h-screen">
          About
        </section>
        <section id="Projects" className="min-h-screen">
          Projects
        </section>
        <section id="Contact" className="min-h-screen">
          Contact Me
        </section>
      </main>
      <Footer />
    </div>
  );
}

export default App;
