import React from "react";
import GridLayout from "react-grid-layout";
import "react-grid-layout/css/styles.css";
import "react-resizable/css/styles.css";
import { GridItem, GridItemWithImage } from "./GridItem";

export const GridContainer = ({ layout, items }) => {
  return (
    <GridLayout className="layout justify-center" layout={layout} cols={4} width={1200} isResizable={false} autoSize={true} draggableHandle=".draggable-handle">
      {items.map((item, index) => {
        if (item.image) {
          return (
              <GridItemWithImage {...item} />
          );
        }
        return (
            <GridItem {...item} />
        );
      })}
    </GridLayout>
  );
};
