import React from "react";
import GridLayout from "react-grid-layout";
import "react-grid-layout/css/styles.css";
import "react-resizable/css/styles.css";
import { GridItem, GridItemWithImage } from "./Card";

export const GridContainer = ({ GridData }) => {
  return (
    <GridLayout className="layout justify-center" layout={layout} cols={4} width={1200} isResizable={false} autoSize={true} draggableHandle=".draggable-handle">
      {items.map((item, index) => {
        if (item.image) {
          return (
            <div key={item.key} data-grid={{ x: 1, y: 0, w: 4, h: 2 }} className="bg-white border border-gray-300 flex flex-col items-center justify-center text-lg draggable-handle">
              <GridItemWithImage {...item} />
            </div>
          );
        }
        return (
          <div key={item.key} data-grid={{ x: 1, y: 0, w: 6, h: 2 }} className="bg-white border border-gray-300 flex flex-col items-center justify-center text-lg draggable-handle">
            <GridItem key={item.key} {...item} />
          </div>
        );
      })}
    </GridLayout>
  );
};
