import React, { useState } from "react";
import "./style.css";

function GetCurrentTheme() {
  return window.matchMedia && window.matchMedia("(prefers-color-scheme: dark)").matches;
}

const DarkMode = () => {
  const [darkMode, setDarkMode] = useState(GetCurrentTheme());

  if (darkMode) {
    document.documentElement.classList.add("dark");
  } else {
    document.documentElement.classList.remove("dark");
  }

  return (
    <input
      type="checkbox"
      className="theme-checkbox appearance-none w-24 h-12 bg-gradient-to-r from-gray-200 to-gray-800 rounded-full relative cursor-pointer transition-all duration-400"
      checked={darkMode}
      onChange={() => setDarkMode(!darkMode)}
    />
  );
};

export default DarkModeButton;
