import React from "react";

function App() {
  function useDarkMode() {
    const [darkMode, setDarkMode] = React.useState(false);
    React.useEffect(() => {
      if (darkMode) {
        document.documentElement.classList.add("dark");
      } else {
        document.documentElement.classList.remove("dark");
      }
      console.log(document.documentElement);
    }, [darkMode]);
    return [darkMode, setDarkMode];
  }
  const [darkMode, setDarkMode] = useDarkMode();

  return (
    <section>
      <button onClick={() => setDarkMode(!darkMode)}>Toggle dark mode</button>

      <div class="bg-white dark:bg-slate-800 rounded-lg px-6 py-8 ring-1 ring-slate-900/5 shadow-xl">
        <div>
          <span class="inline-flex items-center justify-center p-2 bg-indigo-500 rounded-md shadow-lg">
            <svg class="h-6 w-6 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" aria-hidden="true"></svg>
          </span>
        </div>
        <h3 class="text-slate-900 dark:text-white mt-5 text-base font-medium tracking-tight">Writes Upside-Down</h3>
        <p class="text-slate-500 dark:text-slate-400 mt-2 text-sm">The Zero Gravity Pen can be used to write in any orientation, including upside-down. It even works in outer space.</p>
      </div>
      <div class="text-red-900">
        test2
        <div className="grid gap-4 grid-cols-4 border-cyan-900">
          <div className="col-span-1">
          <div></div>1</div>
          <div className="col-span-2">2</div>
          <div className="col-span-1">3</div>
          <div className="col-span-2">4</div>
        </div>
      </div>
    </section>
  );
}

export default App;
